import React, { useEffect, useRef } from "react";
import "ol/ol.css";
import { Map, View } from "ol";
import { Tile as TileLayer } from "ol/layer";
import { OSM } from "ol/source";
import { fromLonLat } from "ol/proj";
import { Feature } from "ol";
import { Point } from "ol/geom";
import { Vector as VectorSource } from "ol/source";
import { Vector as VectorLayer } from "ol/layer";
import { Style, Icon, Fill, Stroke, Circle as CircleStyle } from "ol/style";
import { useLocalStorage } from "./useLocalStorage"; // custom hook for localStorage
import ZoomControl from "ol/control/Zoom";

// Sample data
const pointsData = [
  {
    id: 1,
    name: "Point A",
    status: "open",
    coordinates: [30.5, 50.5],
    comment: "Initial Comment A",
  },
  {
    id: 2,
    name: "Point B",
    status: "closed",
    coordinates: [30.6, 50.6],
    comment: "Initial Comment B",
  },
];

const getColorForStatus = (status) => {
  return status === "open" ? "green" : "red";
};

const MapComponent = () => {
  const mapRef = useRef();
  const [points, setPoints] = useLocalStorage("points", pointsData); // Store data in localStorage

  useEffect(() => {
    const map = new Map({
      target: mapRef.current,
      layers: [
        new TileLayer({
          source: new OSM(),
        }),
      ],
      controls: [new ZoomControl()],
      view: new View({
        center: fromLonLat([30.5, 50.5]), // Default center
        zoom: 6,
      }),
    });

    const vectorSource = new VectorSource();
    points.forEach((point) => {
      const feature = new Feature({
        geometry: new Point(fromLonLat(point.coordinates)),
      });
      feature.setStyle(
        new Style({
          image: new CircleStyle({
            radius: 6,
            fill: new Fill({ color: getColorForStatus(point.status) }),
            stroke: new Stroke({ color: "#fff", width: 1 }),
          }),
        })
      );
      feature.setId(point.id);
      vectorSource.addFeature(feature);
    });

    const vectorLayer = new VectorLayer({
      source: vectorSource,
    });

    map.addLayer(vectorLayer);

    // Fit the map to show all points
    map.getView().fit(vectorSource.getExtent(), {
      padding: [50, 50, 50, 50],
      maxZoom: 14,
    });

    // Point click interaction
    map.on("singleclick", function (event) {
      map.forEachFeatureAtPixel(event.pixel, function (feature) {
        const pointId = feature.getId();
        const clickedPoint = points.find((point) => point.id === pointId);
        const newComment = prompt("Update Comment:", clickedPoint.comment);
        if (newComment !== null) {
          const updatedPoints = points.map((point) =>
            point.id === pointId ? { ...point, comment: newComment } : point
          );
          setPoints(updatedPoints);
        }
      });
    });
  }, [points, setPoints]);

  return <div ref={mapRef} className="map" style={{ height: "100vh", width: "100%" }}></div>;
};

export default MapComponent;
